	int TestProgram::readlockbit(int UutOffsetIndex)
	{	
		int ret		= 0;
		int data	= 0;
		int reg_val	= 0;
		//read lock bit

		//if(!USE_SPI)
		//{
		//	ret = RunVectorFile(UutOffsetIndex, "write_trimlock_init");
		//	CheckError(UutOffsetIndex, ret, __LINE__);
		//	ret = ReadVector(UutOffsetIndex, "read_p2_reg48", data);
		//	CheckError(UutOffsetIndex, ret, __LINE__);
		//}
		//else
		//{
		ret = OnOffSPI(UutOffsetIndex, 1);
		CheckError(UutOffsetIndex, ret, __LINE__);
		//ret = RunSPI(UutOffsetIndex, "write_trimlock_init");
		//CheckError(UutOffsetIndex, ret, __LINE__);
		//ret = ReadSPI(UutOffsetIndex, "read_p2_reg48", data);
		//CheckError(UutOffsetIndex, ret, __LINE__);
		ret = WriteSPI(UutOffsetIndex,3,8, 0x01); //write_trimlock_init
		CheckError(UutOffsetIndex, ret, __LINE__);
		ret = ReadReg(UutOffsetIndex,3, 4, reg_val); // Trimlockread	Bit 7 //ABSTRIMREAD	Bit5~0
		CheckError(UutOffsetIndex, ret, __LINE__);
		data = reg_val;
		_glob->trimlock[UutOffsetIndex] = (data >> 7) & 0x1;
		if (_glob->trimlock[UutOffsetIndex] == 1)
		{
			_glob->FuseBlown[UutOffsetIndex] = 1;
		}

		data = reg_val;
		_glob->iabs_blown_fuse[UutOffsetIndex] = data & 0x3F;

		data = reg_val;
		_glob->testpass[UutOffsetIndex] = (data >> 6) & 0x1;

		ret = ReadReg(UutOffsetIndex,3, 5, reg_val); // Vreftrimread	Bit 7~4 //ATEMPTRIMREAD	Bit 3~0
		CheckError(UutOffsetIndex, ret, __LINE__);

		data = reg_val;
		_glob->vref_blown_fuse[UutOffsetIndex] = (data >> 4) & 0x0F;
		data = reg_val;
		_glob->atemp_blown_fuse[UutOffsetIndex] = data  & 0x0F;

		ret = WriteSPI(UutOffsetIndex,3,8, 0x02); //write_trimlock_init
		CheckError(UutOffsetIndex, ret, __LINE__);
		ret = ReadReg(UutOffsetIndex,3, 4, reg_val); // ATETRIMREAD	Bit 6 //FHTRMMREAD	Bit6~4	//FHTRMLREAD	Bit3~0
		CheckError(UutOffsetIndex, ret, __LINE__);

		data = reg_val;
		_glob->trimhtslope_blown_fuse[UutOffsetIndex] = (data >> 4) & 0x07;

		data = reg_val;
		_glob->htr_blown_fuse[UutOffsetIndex] = data & 0x0F;

		ret = WriteSPI(UutOffsetIndex,3,8, 0x03); //write_trimlock_init
		CheckError(UutOffsetIndex, ret, __LINE__);
		ret = ReadReg(UutOffsetIndex,3, 5, reg_val); // FHTRMHREAD	Bit 7~0
		CheckError(UutOffsetIndex, ret, __LINE__);
		data = reg_val;

		_glob->trimhead_blown_fuse[UutOffsetIndex] = data;

		ret = OnOffSPI(UutOffsetIndex, 0);
		CheckError(UutOffsetIndex, ret, __LINE__);
		//}


	
		return ret;
	}
