				CheckError(idx, ret, __LINE__);
				ret = dm[idx]->DPINOff("WDY");
				CheckError(idx, ret, __LINE__);
				ret = dm[idx]->DPINOff("RWN");
				CheckError(idx, ret, __LINE__);
				ret = dm[idx]->DPINOff("SPD");
				CheckError(idx, ret, __LINE__);
