				CheckError(idx, ret, __LINE__);
				ret = dm[idx]->DPINOn("WDX");
				CheckError(idx, ret, __LINE__);
				ret = dm[idx]->DPINOn("WDY");
				CheckError(idx, ret, __LINE__);
				ret = dm[idx]->DPINOn("RWN");
				CheckError(idx, ret, __LINE__);
