					CheckError(idx, ret, __LINE__);

					ret = dm[idx]->ConfigurePMUCurrentLevel(Drive_Pin_Hi, 0);
					CheckError(idx, ret, __LINE__);
					ret = dm[idx]->ConfigurePMUSamplingTime(Drive_Pin_Hi, NPLC, DM_CONST_PLC);
					CheckError(idx, ret, __LINE__);
					ret = dm[idx]->DPINOn(Drive_Pin_Hi);
