					ret = smu[idx]->SetBandwidth(Drive_Pin_Lo, 1);
					CheckError(idx, ret, __LINE__);
					ret = smu[idx]->ConfigureSamplingTime(Drive_Pin_Lo, NPLC, AM_CONST_PLC);
					CheckError(idx, ret, __LINE__);
					ret = smu[idx]->ClampVoltage(Drive_Pin_Lo, Math::Abs(Clamp_Voltage_V));
