			CheckError(idx, ret, __LINE__);
			ret = dm[idx]->Force("HTR0_F", DM_CONST_FORCE_STATE_PMU);		
			CheckError(idx, ret, __LINE__);
			ret = dm[idx]->ConfigurePMUSamplingTime("HTR0_F", NPLC, DM_CONST_PLC);	
