			double vil;
			double voh; 
			double vol;
			double ioh;
			double iol;
			double vch;
			double vcl;
			double vt;

			vih = 2.5;
			vil = 0.00;
			voh = 0.75; 
			vol = 0.75;
			ioh = 0.002;
			iol = 0.002;
			vch = 6;
			vcl = -0.1;
			vt = 0;

			ret = dm[idx]->Force("RWN", DM_CONST_FORCE_STATE_VECTOR);
			CheckError(idx, ret, __LINE__);
