				//ret = ReadReg(idx, 1, 15, data);//expect 12
				//ret = ReadReg(idx, 3, 13, data);//expect 0
				//ret = ReadReg(idx, 1, 1, data);//expect 192
				//ret = OnOffSPI(idx, 0);
				
