			int Measure_Mode		= 0;
			double RWOpen			= CONST_Rw + CONST_Rwo;			
			int local_barrierCount	= (int)(tf_TestItemCondition_Cast("barrier_index"));
			
			String ^ Test_Pin_PN	= "GRP1_DIFF_SENSE";

