			ret = DriveDMPin(idx, TPDispName[0]);
			CheckError(idx, ret, __LINE__);			
			
			//Configure P_H0
			ret = dm[idx]->Force(Test_Pin_P_H0, DM_CONST_FORCE_STATE_PMU);
