			//Setup Power supplies
			//ret = smu[idx]->SetBandwidth("VCC", Bandwidth);
			//CheckError(idx, ret, __LINE__);
			//ret = smu[idx]->ClampCurrent("VCC", Clamp_Vcc_Current_A);
