			//ret = smu[idx]->DriveVoltage("VCC", 0);
			//CheckError(idx, ret, __LINE__);
			//ret = smu[idx]->OnSmuPin("VCC", true, false);
			//CheckError(idx, ret, __LINE__);

			//ret = smu[idx]->SetBandwidth("VEE", Bandwidth);
			//CheckError(idx, ret, __LINE__);
			//ret = smu[idx]->ClampCurrent("VEE", Clamp_Vcc_Current_A);
