			//Digitizer_GetVpp_bk(idx, _glob->digitizer_setting, diff2se_digitizer_factor, dataArray_voutpp, vpp);

			ret = Digitizer_CaptureVppNoFFT(idx, _glob->digitizer_setting, diff2se_digitizer_factor, vout_pp, dataArray_voutpp);
