					CheckError(idx, ret, __LINE__);
					//ret = dm[idx]->AcquireChannelFirstFailVectorCount("FLTMUX", FLTMUX_debug1);
					//CheckError(idx, ret, __LINE__);
					//ret = dm[idx]->AcquireChannelFirstFailVectorCount("SPD", SPD_debug1);
					//CheckError(idx, ret, __LINE__);
