			CheckError(idx, ret, __LINE__);

			//Power down supplies
			//ret = smu[idx]->DriveVoltage("VEE", 0);
			//CheckError(idx, ret, __LINE__);
			ret = smu[idx]->DriveVoltage("VCC", 0);
			CheckError(idx, ret, __LINE__);

