			CheckError(idx, ret, __LINE__);
			ret = dm[idx]->ConfigurePEAttribute("SPC", 1, 0, 0, 0); 
			CheckError(idx, ret, __LINE__);
			ret = dm[idx]->DPINLevel("SPC", vih, vil, voh, vol, ioh, iol, vch, vcl, vt);
			CheckError(idx, ret, __LINE__);
			ret = dm[idx]->DPINOn("SPC");
			CheckError(idx, ret, __LINE__);
			
