			//ret = dm[idx]->ConfigurePMUOutputFunction("FLTMUX", DM_CONST_DVCI);
			//ret = dm[idx]->ConfigurePMUCurrentLimitRange("FLTMUX", 0.002);
			//ret = dm[idx]->ConfigurePMUVoltageLevel("FLTMUX", 0);
