			ret = dm[idx]->ConfigurePMUOutputFunction("FLTMUX", DM_CONST_DICV);
			CheckError(idx, ret, __LINE__);
			ret = dm[idx]->ConfigurePMUVoltageLimit("FLTMUX", Clamp_Vo_V, -2);
			CheckError(idx, ret, __LINE__);
